import { useState, useEffect } from "react";
import "./App.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import axios from "axios";

import { Tabs, TabsContent, TabsList, TabsTrigger } from "./components/ui/tabs";
import { Button } from "./components/ui/button";
import { Input } from "./components/ui/input";
import { Label } from "./components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "./components/ui/card";
import { Badge } from "./components/ui/badge";
import { Progress } from "./components/ui/progress";
import { Alert, AlertDescription } from "./components/ui/alert";
import { Textarea } from "./components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "./components/ui/select";
import { CheckCircle, XCircle, Clock, Download, Database, Activity, FileText, Beaker } from "lucide-react";

// ✅ Backend URL setup
const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://127.0.0.1:8001";
const API = `${BACKEND_URL}/api`;

console.log("✅ Using backend at:", BACKEND_URL);

const Dashboard = () => {
  const [databases, setDatabases] = useState([]);
  const [jobs, setJobs] = useState([]);
  const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(false);

  const [singleForm, setSingleForm] = useState({
    accession_id: "",
    database: "",
    validate_format: true,
  });

  const [batchForm, setBatchForm] = useState({
    accession_ids: "",
    database: "",
    validate_format: true,
  });

  useEffect(() => {
    loadDatabases();
    loadJobs();
    loadStats();

    const interval = setInterval(() => {
      loadJobs();
      loadStats();
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  // ✅ Load available databases
  const loadDatabases = async () => {
    try {
      const response = await axios.get(`${API}/databases`);
      setDatabases(response.data);
    } catch (error) {
      console.error("❌ Failed to load databases:", error);
      alert("Could not load databases. Make sure backend is running on port 8001 and the route /api/databases exists.");
    }
  };

  // ✅ Load recent jobs
  const loadJobs = async () => {
    try {
      const response = await axios.get(`${API}/jobs?limit=20`);
      setJobs(response.data);
    } catch (error) {
      console.error("❌ Failed to load jobs:", error);
    }
  };

  // ✅ Load stats
  const loadStats = async () => {
    try {
      const response = await axios.get(`${API}/stats`);
      setStats(response.data);
    } catch (error) {
      console.error("❌ Failed to load stats:", error);
    }
  };

  // ✅ Single accession download
  const handleSingleDownload = async () => {
    if (!singleForm.accession_id || !singleForm.database) return;
    setLoading(true);

    try {
      await axios.post(`${API}/batch-download`, {
        accession_ids: [singleForm.accession_id],
        database: singleForm.database,
        validate_format: singleForm.validate_format,
      });

      setSingleForm({ accession_id: "", database: "", validate_format: true });
      loadJobs();
    } catch (error) {
      console.error("❌ Download failed:", error);
      alert("Download failed: " + (error.response?.data?.detail || error.message));
    } finally {
      setLoading(false);
    }
  };

  // ✅ Batch download
  const handleBatchDownload = async () => {
    if (!batchForm.accession_ids || !batchForm.database) return;
    setLoading(true);

    try {
      const accession_ids = batchForm.accession_ids
        .split("\n")
        .map((id) => id.trim())
        .filter((id) => id.length > 0);

      await axios.post(`${API}/batch-download`, {
        ...batchForm,
        accession_ids,
      });

      setBatchForm({ accession_ids: "", database: "", validate_format: true });
      loadJobs();
    } catch (error) {
      console.error("❌ Batch download failed:", error);
      alert("Batch download failed: " + (error.response?.data?.detail || error.message));
    } finally {
      setLoading(false);
    }
  };

  // ✅ Status icon logic
  const getStatusIcon = (status) => {
    switch (status) {
      case "completed":
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case "failed":
        return <XCircle className="h-4 w-4 text-red-500" />;
      case "downloading":
        return <Clock className="h-4 w-4 text-blue-500 animate-pulse" />;
      default:
        return <Clock className="h-4 w-4 text-gray-500" />;
    }
  };

  const formatFileSize = (bytes) => {
    if (!bytes) return "N/A";
    const sizes = ["B", "KB", "MB", "GB"];
    const i = Math.floor(Math.log(bytes) / Math.log(1024));
    return `${(bytes / Math.pow(1024, i)).toFixed(1)} ${sizes[i]}`;
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center gap-3 mb-4">
            <div className="p-2 bg-blue-600 rounded-lg">
              <Beaker className="h-8 w-8 text-white" />
            </div>
            <div>
              <h1 className="text-3xl font-bold text-gray-900">BioFetch</h1>
              <p className="text-gray-600">Unified Bioinformatics Data Downloader</p>
            </div>
          </div>
        </div>

        {/* Tabs */}
        <Tabs defaultValue="download" className="space-y-6">
          <TabsList className="grid w-full grid-cols-3">
            <TabsTrigger value="download">Download</TabsTrigger>
            <TabsTrigger value="jobs">Jobs</TabsTrigger>
            <TabsTrigger value="databases">Databases</TabsTrigger>
          </TabsList>

          {/* Download Tab */}
          <TabsContent value="download" className="space-y-6">
            {/* Single + Batch */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Single */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Download className="h-5 w-5" /> Single Download
                  </CardTitle>
                  <CardDescription>Download a single accession ID file.</CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <Input
                    placeholder="e.g., SRR000001, NC_045512"
                    value={singleForm.accession_id}
                    onChange={(e) => setSingleForm({ ...singleForm, accession_id: e.target.value })}
                  />
                  <Select
                    value={singleForm.database}
                    onValueChange={(v) => setSingleForm({ ...singleForm, database: v })}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select database" />
                    </SelectTrigger>
                    <SelectContent>
                      {databases.map((db) => (
                        <SelectItem key={db.id} value={db.id}>
                          {db.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <Button
                    onClick={handleSingleDownload}
                    disabled={loading || !singleForm.accession_id || !singleForm.database}
                    className="w-full"
                  >
                    {loading ? "Starting..." : "Download"}
                  </Button>
                </CardContent>
              </Card>

              {/* Batch */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <FileText className="h-5 w-5" /> Batch Download
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <Textarea
                    placeholder={"SRR000001\nSRR000002\nSRR000003"}
                    rows={4}
                    value={batchForm.accession_ids}
                    onChange={(e) => setBatchForm({ ...batchForm, accession_ids: e.target.value })}
                  />
                  <Select
                    value={batchForm.database}
                    onValueChange={(v) => setBatchForm({ ...batchForm, database: v })}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select database" />
                    </SelectTrigger>
                    <SelectContent>
                      {databases.map((db) => (
                        <SelectItem key={db.id} value={db.id}>
                          {db.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <Button
                    onClick={handleBatchDownload}
                    disabled={loading || !batchForm.accession_ids || !batchForm.database}
                    className="w-full"
                  >
                    {loading ? "Starting batch..." : "Batch Download"}
                  </Button>
                </CardContent>
              </Card>
            </div>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
};

function App() {
  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Dashboard />} />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
